if (creator.multi.main === newTag || creator.multi._key[newTag]) {
	throw "Attempt to change to existing language tag in creator";
}
if (!oldTag || oldTag === creator.multi.main) {
	creator.multi.main = newTag;
} else if (creator.multi._key[oldTag]) {
	creator.multi._key[newTag] = creator.multi._key[oldTag];
	delete creator.multi._key[oldTag];
