if (lastNameValue === this._defaultLastName) {
	lastNameValue = '';
}
if (firstNameValue === this._defaultFirstName) {
	firstNameValue = '';
}
var variants = this.getNameParticleVariants(lastNameValue, firstNameValue);
return [lastNameValue, firstNameValue, variants];


